// Extract page title and URL
const pageTitle = document.title;
const pageUrl = window.location.href;
const pageContent = document.body.innerText.substring(0, 2000); // Get first 2000 chars of content

// Store in window for AI to access
window.pageToSummarize = {
  title: pageTitle,
  url: pageUrl,
  content: pageContent
};

console.log('Page data ready for summarization');
